function diagramme_bifurcation()global k k1 k2 m l l1 l2 Longueur l0 A omega x0 x1 x2 timem=5;k1=5;k2=10;l=10;k=0;A=0;omega=1;l1=l/2;l2=l/2;x0 = [1.7 0];x1 = [1.7 1/1000];x2 = [1.7 2/1000];time = [0:2*pi*10:2*pi*200];figure('NumberTitle','off','Name','Le pendule à double ressort','Renderer','OpenGL','Color','w','Position',[100 100 500 500])options = odeset('RelTol',1e-6);for A=0:10:500    A/500*100      [t0,theta0] = ode45('pendulum_double_ressort',time,x0,options);    [t1,theta1] = ode45('pendulum_double_ressort',time,x1,options);    [t2,theta2] = ode45('pendulum_double_ressort',time,x2,options);      for i=4:max(size(t0));    plot(A,theta0(i,2),'.','Color','r','MarkerSize',10);    plot(A,theta1(i,2),'.','Color','b','MarkerSize',10);    plot(A,theta2(i,2),'.','Color','g','MarkerSize',10);    drawnow;    hold on;       end  endend