function diagramme_bifurcation()global k k1 k2 m l l1 l2 Longueur l0 A omega x0 x1 x2 timem=5;k1=5;k2=10;l=5;k=2;A=0;omega=0.1;l1=l/2;l2=l/2;x0 = [1.8 0];x1 = [1.7 0.1];x2 = [1.6 0.002];time=[100:50:500];figure('NumberTitle','off','Name','Le pendule à double ressort','Renderer','OpenGL','Color','w','Position',[100 100 500 500])options = odeset('RelTol',1e-6);for A=0:5:300    A/300*100    [t,theta] = ode45('pendulum_double_ressort',time,x0,options);  for i=1:max(size(t));    plot(A,theta(i,2),'.','Color','r','MarkerSize',5);    hold on;  end  [t,theta] = ode45('pendulum_double_ressort',time,x1,options);   for i=1:max(size(t));      plot(A,theta(i,2),'.','Color','b','MarkerSize',5);     hold on;   end    [t,theta] = ode45('pendulum_double_ressort',time,x2,options);   for i=1:max(size(t));      plot(A,theta(i,2),'.','Color','g','MarkerSize',5);     hold on;   endendend